				if (dic_test>0) {
					if (this.canDicPageForw()) {
						this.doDicPageForw();
						--dic_test
					} else {
						dic_test = 0;
					}
					if (dic_test==0)
						kbook.model.processed(55);
					model.setVariable("VAR_DIC_TEST",dic_test)
				} else if (dic_test<0) {
					var action
					var dic_index = model.getVariable("VAR_DIC_INDEX")
