						action = "end"
					} else if (dic_test!=-2 && this.canDicPageForw()) {
						this.doDicPageForw();
					} else {
						var scale = model.editor.getScale()
						if (dic_test== -3) {
							action="end"
						} else if (scale < 4) { //XXL==4
