									} else {
										action="end" //error
									}
								} else {
									action="end"
								}
							}
						}
					}
					if (action=="reopen") {
						model.reopen()
						model.setDictionaryScale(-1)
						this.updateDictionaryViewer()
						if (dic_test!=-2)
							this.setIndex(dic_index)
